import React, { useState } from 'react';

function Sidebar() {
    const [leftMenuActive, setLeftMenuActive] = useState(false);
    const [rightMenuActive, setRightMenuActive] = useState(false);

    const handleLeftButtonClick = () => {
        setLeftMenuActive(!leftMenuActive);
    };

    const handleRightButtonClick = () => {
        setRightMenuActive(!rightMenuActive);
    };

    return (
        <>
            {/* Left Sidebar */}
            <div className={`leftMenuContainer ${leftMenuActive ? 'active' : ''}`}>
                <div className="leftArrow">
                    <button onClick={handleLeftButtonClick}>
                        {leftMenuActive ? 'Close' : 'Open'}
                    </button>
                </div>
                <div className="content">
                    {/* Your sidebar content */}
                </div>
            </div>

            {/* Right Sidebar */}
            <div className={`rightMenuContainer ${rightMenuActive ? 'active' : ''}`}>
                <div className="rightArrow">
                    <button onClick={handleRightButtonClick}>
                        {rightMenuActive ? 'Close' : 'Open'}
                    </button>
                </div>
                <div className="content">
                    {/* Your sidebar content */}
                </div>
            </div>
        </>
    );
}

export default Sidebar;
