import React, { useState } from 'react';

function Sidebar() {
    const [leftMenuActive, setLeftMenuActive] = useState(false);
    const [rightMenuActive, setRightMenuActive] = useState(false);

    const handleLeftButtonClick = () => {
        setLeftMenuActive(!leftMenuActive);
    };

    const handleRightButtonClick = () => {
        setRightMenuActive(!rightMenuActive);
    };

    const handleDragStart = (event) => {
        event.dataTransfer.setData('text/plain', event.target.id);
    };

    return (
        <>
            {/* Left Sidebar */}
            <div className={`sidebar leftSidebar ${leftMenuActive ? 'open' : ''}`}>
                <button className="toggleButton" onClick={handleLeftButtonClick}>
                    {leftMenuActive ? 'Close' : 'Open'}
                </button>
                <div className="content">
                    <button id="item1" draggable="true" onDragStart={handleDragStart}>Item 1</button>
                    <button id="item2" draggable="true" onDragStart={handleDragStart}>Item 2</button>
                    <button id="item3" draggable="true" onDragStart={handleDragStart}>Item 3</button>
                </div>
            </div>

            {/* Right Sidebar */}
            <div className={`sidebar rightSidebar ${rightMenuActive ? 'open' : ''}`}>
                <button className="toggleButton" onClick={handleRightButtonClick}>
                    {rightMenuActive ? 'Close' : 'Open'}
                </button>
                <div className="content">
                    <button id="item4" draggable="true" onDragStart={handleDragStart}>Item 1</button>
                    <button id="item5" draggable="true" onDragStart={handleDragStart}>Item 2</button>
                    <button id="item6" draggable="true" onDragStart={handleDragStart}>Item 3</button>
                </div>
            </div>
        </>
    );
}

export default Sidebar;
