import React, { useState } from 'react';

function Sidebar() {
    const [leftMenuActive, setLeftMenuActive] = useState(false);
    const [rightMenuActive, setRightMenuActive] = useState(false);

    const handleLeftButtonClick = () => {
        setLeftMenuActive(!leftMenuActive);
    };

    const handleRightButtonClick = () => {
        setRightMenuActive(!rightMenuActive);
    };

    return (
        <>
            {/* Left Arrow */}
            <div className="leftArrow">
                <button id="leftBtn" onClick={handleLeftButtonClick}>
                    <i className="fa-solid fa-arrow-right"></i>
                </button>
            </div>

            {/* Left Arrow Menu */}
            <div className={`leftMenuContainer ${leftMenuActive ? 'active' : ''}`}>
                <div className="itemName">
                    <h3>Item 1</h3>
                </div>
                Item 2, Item 3
            </div>

            {/* Right Arrow */}
            <div className="rightArrow">
                <button id="rightBtn" onClick={handleRightButtonClick}>
                    <i className="fa-solid fa-arrow-left"></i>
                </button>
            </div>

            {/* Right Arrow Menu */}
            <div className={`rightMenuContainer ${rightMenuActive ? 'active' : ''}`}>
                <div className="itemName">
                    <h3>Item 1</h3>
                </div>
                Item 2, Item 3
            </div>
        </>
    );
}

export default Sidebar;
